     console.log('apertei em algum botão, cê acredita.')
}

class App extends Component() {
     render() {
          <div>
           <button onClick={eventClick}> Azul </button>
     <button onClick={eventClick}>Vermelho</button>
     <button onClick={eventClick}>Amarelo</button>    
          </div>

     }
}

export default App;
