import React from 'react';


class App extends React.Component {
 constructor() {
  super()
  this.eventClickOne = this.eventClickOne.bind(this)
  this.eventClickTwo = this.eventClickTwo.bind(this)
  this.eventClickThree = this.eventClickThree.bind(this)

  this.state = {
   clickEventOne: 0,
   clickEventTwo: 0,
   clickEventThree: 0,
  }

 }

 eventClickOne() {
  this.setState((currentValue) => ({
   clickEventOne: currentValue.clickEventOne + 1,
  }))
  console.log(`A cor nesse botão é ${this.eventColor(this.state.clickEventOne + 1)}`)
 }

 eventClickTwo() {
  this.setState((currentValue) => ({
   clickEventTwo: currentValue.clickEventTwo + 1,
  }))
  console.log(`A cor nesse botão é ${this.eventColor(this.state.clickEventOne + 1)}`)
 }

 eventClickThree() {
  this.setState((currentValue) => ({
   clickEventThree: currentValue.clickEventThree + 1,
  }))
  console.log(`A cor nesse botão é ${this.eventColor(this.state.clickEventOne + 1)}`)
 }

 eventColor(value) {
  return value % 2 === 0 ? 'green' : 'white'
 }

 render() {

  const { clickEventOne, clickEventTwo, clickEventThree } = this.state

  return (
   <div>
    <button onClick={this.eventClickOne}
     style={{ backgroundColor: this.eventColor(clickEventOne) }}>
     xablau {this.state.clickEventOne}</button>

    <button onClick={this.eventClickTwo}
     style={{ backgroundColor: this.eventColor(clickEventTwo) }}>irineu {this.state.clickEventTwo}</button>

    <button onClick={this.eventClickThree}
     style={{ backgroundColor: this.eventColor(clickEventThree) }}>miranha {this.state.clickEventThree}</button>

   </div>
  )
 }
}

export default App;
