import React from 'react';


class App extends React.Component {
     constructor() {
          super()
          this.eventClickOne = this.eventClickOne.bind(this)
          this.eventClickTwo = this.eventClickTwo.bind(this)
          this.eventClickThree = this.eventClickThree.bind(this)

          this.state = {
               clickEventOne: 0,
               clickEventTwo: 0,
               clickEventThree: 0,
          }

     }

     eventClickOne() {
          this.setState((currentValue) => ({
               clickEventOne: currentValue.clickEventOne + 1,
          }))
     }

     eventClickTwo() {
          this.setState((currentValue) => ({
               clickEventTwo: currentValue.clickEventTwo + 1,
          }))
     }

     eventClickThree() {
          this.setState((currentValue) => ({
               clickEventThree: currentValue.clickEventThree + 1,
          }))
     }

     eventColor(value) {
          return value % 2 === 0 ? 'green' : 'white'
     }

     render() {
          return (
               <div>
                    <button onClick={this.eventClickOne} >xablau {this.state.clickEventOne}</button>
                    <button onClick={this.eventClickTwo}>irineu {this.state.clickEventTwo}</button>
                    <button onClick={this.eventClickThree}>miranha {this.state.clickEventThree}</button>
               </div>
          )
     }
}

export default App;
