import React from 'react';


class App extends React.Component {
     constructor() {
          super()
          this.eventClickOne = this.eventClickOne.bind(this)
          this.eventClickTwo = this.eventClickTwo.bind(this)
          this.eventClickThree = this.eventClickThree.bind(this)
     }

     eventClickOne = () => {
          console.log('"this"apertei em um botão escrita cor azul, cê acredita.')
     }

     eventClickTwo() {
          console.log('apertei em um botão escrita cor Vermelho, cê acredita.')
     }

     eventClickThree() {
          console.log('xablau...amarelo')
     }
     render() {
          return (
               <div>
                    <button onClick={eventClickOne}>Azul</button>
                    <button onClick={eventClickTwo}>Vermelho</button>
                    <button onClick={eventClickThree}>Amarelo</button>
               </div>
          )
     }
}

export default App;
