import React, { Component } from "react";

class Info extends Component {
     render() {
          const { description, confirm, handle } = this.props;

          return (
               <fieldset>
                    <div>
                         <p>Por que ela é sua fovorita?</p>
                         <textarea
                              name="description"
                              cols="30"
                              rows="10"
                              onChange={handle}
                              value={description} />
                              { description.length === 0 ? '  Informação Vazia  ' : '...' }

        </div>
                    <div>
                         <label htmlFor="confirmação">
                              <input
                                   id="confirm"
                                   type="checkbox"
                                   name="confirm"
                                   onChange={handle}
                                   value={confirm}
                              />Você confirma essa cor?
                         </label>
                    </div>
               </fieldset>
          )
     }
}

export default Info;
