import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.handle = this.handle.bind(this)


    this.state = {
      name: ''
    }
  }

  handle(event) {
    this.setState({
      name: event.target.value,
    })
  }


  render() {
    const { name } = this.state

    return (
      <div>
        <h1>Formul√°rio</h1>
        <form className="form">
          <label htmlFor="name">Nome:
            <input 
            type="text" 
            name="name" 
            value={name} 

            />
          </label>

          <label htmlFor="age">Idade:
            <input type="text" name="age" />
          </label>

          <select name="xablau">
            <option value="">selecione</option>
            <option value="test">test</option>
          </select>

          <textarea name="text" cols="30" rows="10">digite aqui!</textarea>
        </form>
      </div>
    );
  }

}

export default App;
