import React, { Component } from "react";

class infoPerson extends Component {
     render () {
          const { name, handle } = this.props;
          return (
               <div>
                 <label htmlFor="name">Nome:
                   <input
                     type="text"
                     name="name"
                     onChange={handle}
                     value={name}
                   />
                 </label>
               </div>
          )
     }
}

export default infoPerson;
