import React from 'react';
import InfoPerson from './components/infoPerson.jsx';
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.handle = this.handle.bind(this)


    this.state = {
      name: '',
      cor: '',
      descrição: '',
      age: 0,
      confirma: false,
    }
  }

  handle({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    })
  }


  render() {
    const { name, age, cor, descrição, confirma } = this.state;

    return (

      <div>
        <h1>Formulário</h1>
        <form className="form">

          <InfoPerson name={name} age={ age } cor={  cor } handle={this.handle} />


          <fieldset>
            <div>
              <p>Por que ela é sua fovorita?</p>
              <textarea
                name="descrição"
                cols="30"
                rows="10"
                onChange={this.handle}
                value={descrição} />
            </div>
            <div>
              <label htmlFor="confirmação">
                <input
                  id="confirma"
                  type="checkbox"
                  name="confirma"
                  onChange={this.handle}
                  value={confirma}
                />Você confirma essa cor?
              </label>
            </div>
          </fieldset>

          <div>
            <label htmlFor="">adicione uma imagem sua com o tema da cor escolhido</label>
            <input type="file" />
          </div>
        </form>
      </div >
    );
  }

}

export default App;
