import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.handle = this.handle.bind(this)


    this.state = {
      name: '',
      age: 0,
      cor: '',
    }
  }

  handle({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    })
  }


  render() {
    const { name, age, cor } = this.state

    return (
      <div>
        <h1>Formulário</h1>
        <form className="form">
          <div>
          <label htmlFor="name">Nome:
            <input
              type="text"
              name="name"
              onChange={this.handle}
              value={name}
            />
          </label>
          </div>

          <label htmlFor="age">Idade:
            <input
              type="number"
              name="age"
              onChange={this.handle}
              value={age}
            />
          </label>

          <label htmlFor="cores">Qual é a sua cor favorita?
            <select name="cores" onChange={this.handle} value={cor}>
              <option value="">selecione</option>
              <option value="Vermelho">Vermelho</option>
              <option value="Preto">Preto</option>
              <option value="Azul">Azul</option>
            </select>
          </label>

          <textarea name="text" cols="30" rows="10">digite aqui!</textarea>
        </form>
      </div>
    );
  }

}

export default App;
