import React, { Component } from "react";

class InfoPerson extends Component {
  render() {
    const { name, age, color, handle } = this.props;
    console.l
    return (
      <fieldset>
        <legend>Informações Pessoais</legend>
        <div>
          <label htmlFor="name">Nome:
            <input
              type="text"
              name="name"
              onChange={handle}
              value={name}
            />
          </label>
        </div>
        <div>
          <label htmlFor="age">Idade:
            <input
              type="text"
              name="age"
              onChange={handle}
              value={age}
            />
          </label>
        </div>
        <div>
          <label htmlFor="color">Qual é a sua cor favorita?
            <select name="color" onChange={handle} value={color}>
              <option value="">selecione</option>
              <option value="Vermelho">Vermelho</option>
              <option value="Preto">Preto</option>
              <option value="Azul">Azul</option>
            </select>
          </label>
        </div>
      </fieldset>
    )
  }
}

export default InfoPerson;
