import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.handle = this.handle.bind(this)


    this.state = {
      name: '',
      cor: '',
      descrição: '',
      age: 0,
      confirma: false,
    }
  }

  handle({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    })
  }


  render() {
    const { name, age, cor, descrição, confirma } = this.state;

    return (

      <div>
        <h1>Formulário</h1>
        <form className="form">
          <fieldset>
            <legend>Informações Pessoais</legend>
            <div>
              <label htmlFor="name">Nome:
                <input
                  type="text"
                  name="name"
                  onChange={this.handle}
                  value={name}
                />
              </label>
            </div>
            <div>
              <label htmlFor="age">Idade:
                <input
                  type="number"
                  name="age"
                  onChange={this.handle}
                  value={age}
                />
              </label>
            </div>
            <div>
              <label htmlFor="cores">Qual é a sua cor favorita?
                <select name="cores" onChange={this.handle} value={cor}>
                  <option value="">selecione</option>
                  <option value="Vermelho">Vermelho</option>
                  <option value="Preto">Preto</option>
                  <option value="Azul">Azul</option>
                </select>
              </label>
            </div>
          </fieldset>

          <fieldset>

            <div>
              <p>Por que ela é sua fovorita?</p>
              <textarea
                name="descrição"
                cols="30"
                rows="10"
                onChange={this.handle}
                value={descrição} />
            </div>
            <div>
              <label htmlFor="confirmação">
                <input
                  id="confirma"
                  type="checkbox"
                  name="confirma"
                  onChange={this.handle}
                  value={confirma}
                />Você confirma essa cor?
              </label>
            </div>
          </fieldset>

          <div>
            <label htmlFor="">adicione uma imagem sua com o tema da cor escolhido</label>
            <input type="file" />
          </div>
        </form>
      </div >
    );
  }

}

export default App;
