import React, { Component } from "react";

class Info extends Component {
  render() {
const { description, con } = this.props;

    return (
      <fieldset>
        <div>
          <p>Por que ela é sua fovorita?</p>
          <textarea
            name="description"
            cols="30"
            rows="10"
            onChange={this.handle}
            value={description} />
        </div>
        <div>
          <label htmlFor="confirmação">
            <input
              id="confirm"
              type="checkbox"
              name="confirm"
              onChange={this.handle}
              value={confirm}
            />Você confirma essa cor?
          </label>
        </div>
      </fieldset>
    )
  }
}

export default Info;
